package edu.kh.basic;

public class JavaTest2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 사칙연산 연습
		
		int a = 800;
		int b = 4;
		
		System.out.println(a*b);
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a/b);
		System.out.println(a%b); //% : '모듈러' > 나머지 구하기
		System.out.println("800 + 4 = " + (a+b));
 // java는 사칙연산 우선순위 그대로 따름
		System.out.println("문자 " + "이어쓰기 " + "연습");
	}

}
